<?php defined('_FINDEX_') or die('Access Denied'); echo"<table class='table table-striped table-bordered table-highlight'><thead><tr>	<th><center>NO</center></th>	<th>NAMA REKTOR</th>	<th width=''>JABATAN</th>	<th width=''><center>GAJI POKOK</center></th>	<th width=''><center>T. JABATAN</center></th>	<th width=''><center>T. TRANSPORT</center></th>	<th width=''><center>POTONGAN</center></th>	<th width=''><center>TOTAL</center></th></tr></thead>";	$no=0;	$main=_query("SELECT * FROM transaksihnrektor WHERE TglBayar BETWEEN '$dari' AND '$sampai' ORDER BY TglBayar DESC");	while($r=_fetch_array($main)){	$k = GetFields('karyawan', 'id', $r[userid], '*');	$NamaJabatan=GetName("jabatan","Jabatan_ID",$k[Jabatan],"Nama");	$NamaRektor=strtoupper($k['nama_lengkap']);	$gajiPokok=digit($r['gajipokok']);	$Ttransport=digit($r['transport']);	$Tjabatan=digit($r['jabatan']);	$Potongan=digit($r['potongan']);	$tot=$r[Total];	$jumlahTot 	+=$tot;	$total=digit($tot);	$jtotal=digit($jumlahTot);	$no++;	echo "<tr>                            		<td><center>$no</center></td>		<td>$NamaRektor</td>		<td>$NamaJabatan</td>		<td>Rp. <span class='pull-right'>$gajiPokok</span></td>		<td>Rp. <span class='pull-right'>$Tjabatan</span></td>		<td>Rp. <span class='pull-right'>$Ttransport</span></td>		<td>Rp. <span class='pull-right'>$Potongan</span></td>		<td>Rp. <span class='pull-right'>$total</span></td>		</tr>";}			echo"<tfoot><tr><td colspan='7'><b>JUMLAH TOTAL</b></td>	<td><b>Rp. <span class='pull-right'>$jtotal</span></b></td></tr></tfoot></table>";?>