<?phpdefined('_FINDEX_') or die('Access Denied');//Tambah dan Edit Inventarisif(isset($_POST['simpanInventaris'])) {  $md = $_POST['md']+0;  if ($md == 0) {    $update=_query("UPDATE inventaris SET 			NamaInventaris		= '$_POST[NamaInventaris]',			Institusi			= '$_POST[Institusi]',			Kampus				= '$_POST[Kampus]',			Satuan				= '$_POST[Satuan]', 			forsale				= '$_POST[forsale]' 			WHERE InventarisID = '$_POST[InventarisID]'");	alert('info','Inventaris Berhasil Di Update');	CatatLog($_SESSION[Nama],'Update Inventaris','Inventaris Berhasil Di Update');		htmlRedirect('go-inventaris.html',1);  } else {	  $insert =_query("INSERT INTO inventaris (NamaInventaris,Institusi,Kampus,Satuan,forsale)			VALUES('$_POST[NamaInventaris]','$_POST[Institusi]','$_POST[Kampus]','$_POST[Satuan]','$_POST[forsale]')");	alert('info','Inventaris Berhasil Di Simpan');	CatatLog($_SESSION[Nama],'Tambah Inventaris','Inventaris Berhasil Di Simpan');		htmlRedirect('go-inventaris.html',1);  }}// Hapus Inventarisif(isset($_POST['delete'])){	$source = @$_POST['check'];	$source = multipleSelect($source);	if(empty($source)){	alert('error','Pilih inventaris yang Akan di Hapus');	CatatLog($_SESSION[Nama],'Hapus Data inventaris','Gagal Meng-Hapus inventaris Belum Dipilih');	}else{	$del = explode(",",$source);	foreach($del as $id)	{		$tabel="inventaris";		$kondisi="InventarisID='$id'";		$delete = delete($tabel,$kondisi);		}	alert('info','Data Inventaris Berhasil di Hapus');	CatatLog($_SESSION[Nama],'Hapus Data Inventaris','Data Inventaris Berhasil di Hapus');	}}