glE]);	$warna 			= $_POST['warna'];	$aktif 			= $_POST['aktif'];  if ($md == 0) {	$update=_query("UPDATE event SET			TahunID			= '$tahun',			JenisEvent		= '$jenis',			Semester		= '$semester',			NamaEvent		= '$nama',			StartDate		= '$StartDate',			EndDate			= '$EndDate',			warna			= '$warna',			aktif			= '$aktif'			WHERE EventId	= '$id'");	if ($update){	if ($aktif=="Y"){	$updateaLL=_query("UPDATE event SET aktif = 'N'			WHERE TahunID='$tahun' AND Semester NOT IN ('$semester')");		}	}	alert('info','Event Akademik Berhasil di Update');	CatatLog($_SESSION[Nama],'Update Event Akademik','Event Akademik Berhasil di Update');		htmlRedirect('aksi-kalender-Event.html',1);  } else {    $query = "INSERT INTO event(EventId,TahunID,JenisEvent,Semester,NamaEvent,StartDate,EndDate,warna,aktif) 		VALUES('$id','$tahun','$jenis','$semester','$nama','$StartDate','$EndDate','$warna','$aktif')";	$insert=_query($query);	if ($insert){	if ($aktif=="Y"){		$updateaLL=_query("UPDATE event SET aktif = 'N'		WHERE TahunID='$tahun' AND Semester NOT IN ('$semester')");		}	}	alert('info','Event Akademik Berhasil Di Simpan');	CatatLog($_SESSION[Nama],'Tambah Event Akademik','Event Akademik  Berhasil Di Simpan');		htmlRedirect('aksi-kalender-Event.html',1);  }}// Hapus EVENTif(isset($_POST['deleteEvent'])){	$source = @$_POST['check'];	$source = multipleSelect($source);	if(empty($source)){	alert('error','Pilih Event yang Akan di Hapus');	CatatLog($_SESSION[Nama],'Hapus Data Event','Gagal Meng-Hapus Event Belum Dipilih');	}else{	$del = explode(",",$source);	foreach($del as $id)	{		$tabel="event";		$kondisi="EventId='$id'";		$delete = delete($tabel,$kondisi);		}	alert('info','Data Event Berhasil di Hapus');	CatatLog($_SESSION[Nama],'Hapus Data Event','Data Event Berhasil di Hapus');	}}