<?phpdefined('_FINDEX_') or die('Access Denied');//Tambah dan Edit Prodiif(isset($_POST['simpanKampus'])) {	$md			= $_POST['md']+0; if ($md == 0) {		$update=_query("UPDATE kampus SET 				Nama  			= '$_POST[Nama]',				Alamat  		= '$_POST[Alamat]',				Kota  			= '$_POST[Kota]',				Identitas_ID	= '$_POST[institusi]',				Telepon			= '$_POST[Telepon]',				Fax				= '$_POST[Fax]',				Aktif			= '$_POST[Aktif]'		WHERE Kampus_ID			= '$_POST[ID]'");		if($update AND isset($_POST['simpanKampus'])){		alert('info','Data Kampus Berhasil Diupdate');		CatatLog($_SESSION['Nama'],'Update Kampus','Data Kampus Berhasil Diupdate');		htmlRedirect('go-kampus.html',2);		}  } else {	$cek=_num_rows(_query ("SELECT * FROM kampus WHERE Kampus_ID='$_POST[Kampus_ID]' AND Nama='$_POST[Nama]'"));        if ($cek > 0){			alert("error", "Kampus : $_POST[Nama] Sudah ada dalam database");			CatatLog($_SESSION['Nama'],'Tambah Kampus',"Gagal menambah Kampus..Data Sudah ada dalam database");			htmlRedirect('go-kampus.html',2);        }else{	$insert =_query("INSERT INTO kampus(Kampus_ID,Nama,Alamat,Kota,Identitas_ID,Telepon,Fax,Aktif) VALUES('$_POST[Kampus_ID]','$_POST[Nama]','$_POST[Alamat]','$_POST[Kota]','$_POST[institusi]','$_POST[Telepon]','$_POST[Fax]','$_POST[Aktif]')");	if($insert){			alert('info','Data Kampus Berhasil Disimpan');			CatatLog($_SESSION['Nama'],'Tambah Kampus','Data Kampus Berhasil Disimpan');			htmlRedirect('go-kampus.html',2);			}           }  }	}// Hapus Program Kelasif(isset($_POST['delete'])){	$source = @$_POST['check'];	$source = multipleSelect($source);	$delete = multipleDelete('kampus',$source,'Kampus_ID');		if(isset($delete)){		alert('info','Kampus Berhasil di Hapus');		CatatLog($_SESSION['Nama'],'Hapus Kampus','Kampus Berhasil di Hapus');	}else{		alert('error','Pilih Kampus yang Akan di Hapus');		CatatLog($_SESSION['Nama'],'Hapus Kampus','Gagal Menghapus Kampus, Pilih Kampus yang Akan di Hapus');	}}