<?phpdefined('_FINDEX_') or die('Access Denied');//Tambah dan Edit Prodiif(isset($_POST['simpanKelas'])) {	$md			= $_POST['md']+0; if ($md == 0) {	    $update=_query("UPDATE program SET 				Program_ID		= '$_POST[Program_ID]',				nama_program	= '$_POST[nama_program]',				Identitas_ID	= '$_POST[institusi]',				aktif			= '$_POST[aktif]'				WHERE ID		= '$_POST[ID]'");		if($update AND isset($_POST['simpanKelas'])){		alert('info','Data Program Kelas Berhasil Diupdate');		CatatLog($_SESSION['Nama'],'Update Program Kelas','Data Program Kelas Berhasil Diupdate');		htmlRedirect('go-kelas.html',2);		}  } else {	$cek=_num_rows(_query ("SELECT * FROM program WHERE Program_ID='$_POST[Program_ID]'"));        if ($cek > 0){			alert("error", "Kode : $_POST[Program_ID] Sudah ada dalam database");			CatatLog($_SESSION['Nama'],'Tambah Program Kelas',"Gagal menambah Program Kelas..Data Sudah ada dalam database");			htmlRedirect('go-kelas.html',2);        }else{	$insert =_query("INSERT INTO program (Program_ID,nama_program,Identitas_ID,aktif) VALUES('$_POST[Program_ID]','$_POST[nama_program]','$_POST[institusi]','$_POST[aktif]')");	if($insert){			alert('info','Data Program Kelas Berhasil Disimpan');			CatatLog($_SESSION['Nama'],'Tambah Program Kelas','Data Program Kelas Berhasil Disimpan');			htmlRedirect('go-kelas.html',2);			}           }  }	}// Hapus Program Kelasif(isset($_POST['delete'])){	$source = @$_POST['check'];	$source = multipleSelect($source);	$delete = multipleDelete('program',$source,'ID');		if(isset($delete)){		alert('info','program Kelas Berhasil di Hapus');		CatatLog($_SESSION['Nama'],'Hapus Program Kelas','Program Kelas Berhasil di Hapus');	}else{		alert('error','Pilih Program Kelas yang Akan di Hapus');		CatatLog($_SESSION['Nama'],'Hapus Program Kelas','Gagal Menghapus Program Kelas, Pilih Program Kelas yang Akan di Hapus');	}}