<?phpdefined('_FINDEX_') or die('Access Denied');// Import Data Kelulusanif(isset($_POST['ImportKelulusan'])){$identitas=$_SESSION['Identitas'];include "librari/excel_reader.php";	$data = new Spreadsheet_Excel_Reader($_FILES['import_file']['tmp_name']);	$baris = $data->rowcount($sheet_index=0);	$sukses = 0;	$gagal = 0;		for ($i=2; $i<=$baris; $i++)			{ 				$n1 = trimed($data->val($i, 1));// nim				$n2 = trimed($data->val($i, 2));// Tgl Lulus				$n3 = trimed($data->val($i, 3));// NO SKL				$n4 = trimed($data->val($i, 4));// Nilai				$n5 = trimed($data->val($i, 5));// Grade              if(!empty($n1) AND !empty($n2) AND !empty($n3) AND !empty($n4) AND !empty($n5))              {				// cek data, lalu sisipkan ke dalam tabel				$cek=_query("SELECT * FROM mahasiswa WHERE NIM='$n1'");				$cekdata = _num_rows($cek);				if(empty($cekdata)) {				$sukses=false;				}else{				$thnLulus=substr($n2,0,4);				$ipk=GetIpk($n1);				$TotalSKS=GetTotalSKS($n1);				$hasil=_query("UPDATE mahasiswa SET				TahunLulus		= '$thnLulus',				LulusUjian		= 'Y',				NilaiUjian		= '$n4',				GradeNilai		= '$n5',				TanggalLulus	= '$n2',				IPK				= '$ipk',				TotalSKS		= '$TotalSKS',				skl				= '$n3'				WHERE NIM	= '$n1'");				}			if ($hasil) $sukses++; else $gagal++;			$sukses=true;              }  			}if($sukses){	alert("info","Import Data Dosen Selesai Sukses : $sukses | Gagal : $gagal ");	CatatLog($_SESSION[Nama],"Import Data Dosen","Import Data Dosen Selesai | Sukses : $sukses | Gagal : $gagal ");	}else{	alert("error","IMPORT DATA KELULUSAN GAGAL : Tidak Ditemukan Mahasiswa Dengan NIM : $n1 | $sukses | Gagal : $gagal ");	}}