<?phpdefined('_FINDEX_') or die('Access Denied');//Tambah dan Edit Beritaif(isset($_POST['simpanBerita'])) {global $saiki;	$md			= $_POST['md']+0; if ($md == 0) {	    $update=_query("UPDATE berita SET 				judul		= '$_POST[judul]',				isi	= '$_POST[isi]'				WHERE id		= '$_POST[id]'");		if($update AND isset($_POST['simpanBerita'])){		alert('info','Berita Berhasil Diupdate');		CatatLog($_SESSION['Nama'],'Update Berita','Berita Berhasil Diupdate');		htmlRedirect('go-news.html',2);		}  } else {	$insert =_query("INSERT INTO berita (judul,isi,tanggal) VALUES('$_POST[judul]','$_POST[isi]','$saiki')");	if($insert){			alert('info','Berita Berhasil Disimpan');			CatatLog($_SESSION['Nama'],'Tambah Berita','Berita Berhasil Disimpan');			htmlRedirect('go-news.html',2);			}   }	}// Hapus Beritaif(isset($_POST['delete'])){	$source = @$_POST['check'];	$source = multipleSelect($source);	$delete = multipleDelete('berita',$source,'id');		if(isset($delete)){		alert('info','Berita Berhasil di Hapus');		CatatLog($_SESSION['Nama'],'Hapus Berita','Berita Berhasil di Hapus');	}else{		alert('error','Pilih Berita yang Akan di Hapus');		CatatLog($_SESSION['Nama'],'Hapus Berita','Gagal Menghapus Berita, Pilih Program Berita yang Akan di Hapus');	}}