<?phpdefined('_FINDEX_') or die('Access Denied');//Tambah dan Edit Prodiif(isset($_POST['simpanProdi'])) {		$md			= $_POST['md']+0;		$TglSKDikti=sprintf("%02d%02d%02d",$_POST[thn_SKDikti],$_POST[bln_SKDikti],$_POST[tgl_SKDikti]);        $TglSKBAN=sprintf("%02d%02d%02d",$_POST[thn_SKBAN],$_POST[bln_SKBAN],$_POST[tgl_SKBAN]);  if ($md == 0) {	    $update=_query("UPDATE jurusan SET 				Identitas_ID	='$_POST[institusi]',				kode_jurusan	='$_POST[kode_jurusan]',				nama_jurusan	='$_POST[nama_jurusan]',				jenjang			='$_POST[jenjang]',				Akreditasi		='$_POST[Akreditasi]',				NoSKDikti		='$_POST[NoSKDikti]',				TglSKDikti		='$TglSKDikti',				NoSKBAN			='$_POST[NoSKBAN]',				TglSKBAN		='$TglSKBAN',				Aktif			='$_POST[Aktif]'		WHERE jurusan_ID		='$_POST[ID]'");				if($update AND isset($_POST['simpanProdi'])){		alert('info','Data Program Studi Berhasil Diupdate');		CatatLog($_SESSION['Nama'],'Update Program Studi','Data Program Studi Berhasil Diupdate');		htmlRedirect('go-prodi.html',2);		}  } else {	$cek=_num_rows(_query ("SELECT * FROM jurusan WHERE Identitas_ID='$_POST[institusi]' AND kode_jurusan='$_POST[kode_jurusan]'"));        if ($cek > 0){			alert("error", "Kode : $_POST[kode_jurusan] di Institusi $_POST[institusi] Sudah ada dalam database");			CatatLog($_SESSION['Nama'],'Tambah Prodi baru',"Gagal menambah Prodi..Data Sudah ada dalam database");			htmlRedirect('go-prodi.html',2);        }else{	$insert =_query("INSERT INTO jurusan(Identitas_ID,kode_jurusan,nama_jurusan,jenjang,Akreditasi,NoSKDikti,TglSKDikti,NoSKBAN,TglSKBAN,Aktif) VALUES('$_POST[institusi]','$_POST[kode_jurusan]','$_POST[nama_jurusan]','$_POST[jenjang]','$_POST[Akreditasi]','$_POST[NoSKDikti]','$TglSKDikti','$_POST[NoSKBAN]','$TglSKBAN','$_POST[Aktif]')");	if($insert){			alert('info','Data Program Studi Berhasil Disimpan');			CatatLog($_SESSION['Nama'],'Tambah Program Studi','Data Program Studi Berhasil Disimpan');			htmlRedirect('go-prodi.html',2);			}           }  }	}// Hapus Program Studiif(isset($_POST['delete'])){	$source = @$_POST['check'];	$source = multipleSelect($source);	$delete = multipleDelete('jurusan',$source,'jurusan_ID');		if(isset($delete)){		alert('info','Program Studi di Hapus');		CatatLog($_SESSION['Nama'],'Hapus Program Studi','Program Studi Berhasil di Hapus');	}else{		alert('error','Pilih Program Studi yang Akan di Hapus');		CatatLog($_SESSION['Nama'],'Hapus Program Studi','Gagal Menghapus Program Studi, Pilih Program Studi yang Akan di Hapus');	}}