<?phpdefined('_FINDEX_') or die('Access Denied');//Tambah dan Edit Prodiif(isset($_POST['simpanRuang'])) {	$md				= $_POST['md']+0; 	$CekJurusan 	=$_POST['kode_jurusan'];	$jum			=count($CekJurusan);        	$Ruang_ID     	= $_POST['Ruang_ID'];	$Nama			= $_POST['Nama'];	$Kampus_ID     	= $_POST['Kampus_ID'];	$Lantai     	= $_POST['Lantai'];	$RuangKuliah	= $_POST['RuangKuliah'];	$Kapasitas		= $_POST['Kapasitas'];	$KapasitasUjian	= $_POST['KapasitasUjian'];	$Keterangan     = $_POST['Keterangan'];	$Aktif			= $_POST['Aktif'];if ($md == 0) {		$update=_query("UPDATE kampus SET 				Nama  			= '$_POST[Nama]',				Alamat  		= '$_POST[Alamat]',				Kota  			= '$_POST[Kota]',				Identitas_ID	= '$_POST[institusi]',				Telepon			= '$_POST[Telepon]',				Fax				= '$_POST[Fax]',				Aktif			= '$_POST[Aktif]'		WHERE Kampus_ID			= '$_POST[ID]'");		if($update AND isset($_POST['simpanKampus'])){		alert('info','Data Kampus Berhasil Diupdate');		CatatLog($_SESSION['Nama'],'Update Kampus','Data Kampus Berhasil Diupdate');		htmlRedirect('go-kampus.html',2);		}  } else {          $sqlpil= "SELECT * FROM ruang WHERE Ruang_ID='$Ruang_ID'";          $qrypil= _query($sqlpil);          while ($datapil=_fetch_array($qrypil)){			  for($i=0; $i < $jum; ++$i){				  if ($datapil['kode_jurusan'] !=$CekJurusan[$i]){				  $sqldel= "DELETE FROM ruang WHERE Ruang_ID='$Ruang_ID' AND NOT kode_jurusan IN ('$CekJurusan[$i]')";   				  $hapus=_query($sqldel);				  }			  }          }                for($i=0; $i < $jum; ++$i){          $sqlr="SELECT * FROM ruang WHERE Ruang_ID='$Ruang_ID'AND kode_jurusan='$CekJurusan[$i]'";        	$qryr= _query($sqlr);        	$cocok= _num_rows($qryr);            	if (! $cocok==1){                	$sql = "INSERT INTO ruang(Ruang_ID,Nama,Kampus_ID,Lantai,kode_jurusan,RuangKuliah,Kapasitas,KapasitasUjian,Keterangan,Aktif)VALUES ('$Ruang_ID','$Nama','$Kampus_ID','$Lantai','$CekJurusan[$i]','$RuangKuliah','$Kapasitas','$KapasitasUjian','$Keterangan','$Aktif')";			$insert=_query($sql) or die ();          }			}  	if($insert AND isset($_POST['simpanRuang'])){		alert('info','Data Ruang Berhasil Disimpan');		CatatLog($_SESSION['Nama'],'Ruang Kuliah','Data Ruang Berhasil Disimpan');		htmlRedirect('go-ruang.html',2);		} 		} 	}// Hapus Program Ruangif(isset($_POST['delete'])){	$source = @$_POST['check'];	$source = multipleSelect($source);	$delete = multipleDelete('ruang',$source,'ID');		if(isset($delete)){		alert('info','Ruang Berhasil di Hapus');		CatatLog($_SESSION['Nama'],'Hapus Ruang','Ruang Berhasil di Hapus');	}else{		alert('error','Pilih Ruang yang Akan di Hapus');		CatatLog($_SESSION['Nama'],'Hapus Ruang','Gagal Menghapus Ruang, Pilih Ruang yang Akan di Hapus');	}}