idget-content"><table class="table table-striped table-bordered table-highlight" id="tablef">	<thead>		<tr>			<th><center><?php echo $pilihAll; ?></center></th>			<th>Kode</th>			<th>Nama</th>			<th>Prodi</th>			<th>Ruang Kelas ? </th>			<th>Kapasitas</th>			<th>Keterangan</th>			<th><center>Status</center></th>			<?php echo $AksiEdit; ?>		</tr>	</thead>	<tbody><?php	$sql="SELECT * FROM ruang WHERE Kode_Jurusan='$prodi' AND Kampus_ID='$kampus' GROUP BY Ruang_ID ORDER BY ID";	$qry= _query($sql) or die ();	while ($data=_fetch_array($qry)){	$sttus = ($data['Aktif'] == 'Y')? '<i class="icon-ok green"></i>' : '<i class="icon-remove red"></i>';	$kelas = ($data['RuangKuliah'] == 'Y')? '<i class="icon-ok green"></i>' : '<i class="icon-remove red"></i>';	$no++;	$check ="<input type='checkbox' name='check[]' value='$data[ID]'>";	$pilih=($hapus)? $check:$no;	$BtnEdit=($edit)? "<td>						<center>							<a class='btn btn-mini btn-primary' href='actions-ruang-edit-0-$data[ID].html'><i class='icon-pencil'></i> Edit</a>						</center>						</td>":"";	echo"<tr>		<td><center>$pilih</center></td> 		<td>$data[Ruang_ID]</td>		<td>$data[Nama]</td>		<td>$data[Kode_Jurusan]</td>		<td><center>$kelas</center></td>		<td>$data[KapasitasUjian] - $data[Kapasitas]</td>		<td>$data[Keterangan]</td>		<td><center>$sttus</center></td>		$BtnEdit		</tr>";  $no++; 	}  ?>	</tbody></table></div></div></form></div></div><?php }else{ErrorAkses();} ?>