<?php defined('_FINDEX_') or die('Access Denied'); echo"<table class='table table-striped table-bordered table-highlight'><thead><tr>	<th><center>NO</center></th>	<th>TANGGAL</th>	<th width=''>NAMA</th>	<th width=''><center>UKURAN</center></th>	<th width=''><center>JUMLAH</center></th>	<th width=''><center>KETERANGAN</center></th></tr></thead>";	$no=0;	$main=_query("SELECT * FROM transaksi INNER JOIN transaksijual ON transaksi.TransID = transaksijual.Notrans WHERE transaksijual.produkid='4' AND transaksijual.TglBayar BETWEEN '$dari' AND '$sampai' ORDER BY transaksijual.TglBayar DESC");	while($r=_fetch_array($main)){	$tot=$r['Jumlah'] * $r['Harga'];	$harga=digit($tot);	$tgla=tgl_indo($r['TglBayar']);	$ukuran=strtoupper($r['ukuran']);	$ket=($r[Debet]=='0')? "$r[Uraian]":"Rp. <span class='pull-right'>$harga</span>";	$no++;	echo "<tr>                            		<td><center>$no</center></td>		<td>$tgla</td>		<td>$r[userid]</td>		<td><center>$ukuran</center></td>		<td><center>$r[Jumlah]</center></td>		<td>$ket</td>		</tr>";$jumlahTot 	+=$tot;$jtotal=digit($jumlahTot);}			echo"<tfoot><tr><td colspan='5'><b>JUMLAH TOTAL</b></td>	<td><b>Rp. <span class='pull-right'>$jtotal</span></b></td></tr></tfoot></table>";?>