<?php defined('_FINDEX_') or die('Access Denied'); echo"<table class='table table-striped table-bordered table-highlight'><thead>  <tr>    <th rowspan='2'><center>NO</center></th>    <th colspan='2'><center>DOSEN </center></th>    <th colspan='3'><center>MATA KULIAH</center></th>    <th colspan='4'><center>PRESENSI</center></th>    <th rowspan='2'><center>JUMLAH</center></th>  </tr>  <tr>    <th>Nama</th>    <th>Jabatan</th>    <th>Kode </th>    <th>Nama Matakuliah </th>    <th>Sks</th>    <th>Tgl T.Muka</th>    <th>Honor/Sks</th>    <th>Transport/T.Muka</th>    <th>Honor</th>  </tr></thead>";	$main=_query("SELECT * FROM transaksihnrdosen WHERE TahunID='$tahunAkdm' AND semester='$semester' AND TglBayar LIKE '$tahun-$bulan%' ORDER BY userid DESC");	$no=1;	while($r=_fetch_array($main)){	$jbatan=JabatanIdDosen($r[userid]);	$NamaJabatan=JabatanStaff($jbatan);	$NamaDosen=NamaDosen($r[userid]);	$honorSks=digit($r[honorSks]);	$honorTmuka=digit($r[honorTmuka]);	$totalHonor=digit($r[TotalHonor]);	$potong=digit($r[potongan]);	$totalall=digit($r[Total]);	//Presensi	$na=1;	$presensi="SELECT * FROM presensi 		INNER JOIN matakuliah ON presensi.mtkID=matakuliah.Matakuliah_ID		WHERE presensi.DosenID='$r[userid]' 		AND presensi.TahunID='$r[TahunID]' 		AND presensi.semester='$r[semester]' 		AND presensi.Pertemuan='1' 		AND presensi.Tanggal BETWEEN '$r[periode1]' AND '$r[periode2]' 		ORDER BY presensi.Tanggal DESC";	$press=_query($presensi) or die();	while($data=_fetch_array($press))	{		$tgle=format_tanggal($data[Tanggal]);		$MKK=KelompokMtk($data['KelompokMtk_ID']);		$totalsks=$data[SKS] * $r[honorSks];		$tot=$totalsks + $r[honorTmuka];		$toot=digit($tot);	$nomere=($na==1)? $no:"";	$namae=($na==1)? $NamaDosen:"";	$jabatane=($na==1)? $NamaJabatan:"";	$totalalle=($na==$r['TatapMuka'])? "Rp. <span class='pull-right'>$totalall</span>":"";	echo "<tr>    <td><center>$nomere</center></td>    <td>$namae</td>    <td>$jabatane</td>    <td>$MKK $data[Kode_mtk]</td>    <td>$data[Nama_matakuliah]</td>    <td><center>$data[SKS]</center></td>    <td>$tgle</td>    <td>Rp. <span class='pull-right'>$honorSks</span></td>    <td>Rp. <span class='pull-right'>$honorTmuka</span></td>    <td>Rp. <span class='pull-right'>$toot</span></td>    <td>$totalalle</td>  </tr>";$na++;}$totals+=$r[Total];$JumLahTotal=digit($totals);$no++;}			echo"<tfoot>	<tr>		<td colspan='10'><b><center>JUMLAH TOTAL</center></b></td>		<td><b>Rp. <span class='pull-right'>$JumLahTotal</span></b></td>	</tr>	</tfoot></table>";	?>