<?php defined('_FINDEX_') or die('Access Denied'); echo"<table class='table table-striped table-bordered table-highlight'><thead><tr>		<th><center>NO</center></th>		<th>NAMA</th>		<th>JABATAN</th>		<th>GAJI POKOK</th>		<th colspan='2'><center>UANG MAKAN</center></th>		<th colspan='2'><center>UANG LEMBUR</center></th>		<th>POTONGAN</th>		<th><center>JUMLAH</center></th></tr></thead>";	$no=0;	$main=_query("SELECT * FROM transaksihnrstaff WHERE TglBayar BETWEEN '$dari' AND '$sampai' ORDER BY TglBayar DESC");	while($r=_fetch_array($main)){	$k = GetFields('karyawan', 'id', $r[userid], '*');	$NamaJabatan=GetName("jabatan","Jabatan_ID",$k[Jabatan],"Nama");	$NamaRektor=strtoupper($k['nama_lengkap']);	$gajiPokok=digit($r['gajipokok']);	$uangmakan=digit($r['uangmakan']);	$uanglembur=digit($r['uanglembur']);	$Potongan=digit($r['potongan']);	$tot=$r[Total];	$jumlahTot 	+=$tot;	$total=digit($tot);	$jtotal=digit($jumlahTot);	$no++;	echo "<tr>                            		<td><center>$no</center></td>		<td>$NamaRektor</td>		<td>$NamaJabatan</td>		<td>Rp. <span class='pull-right'>$gajiPokok</span></td>		<td>$r[hari] <span class='pull-right'>Hari</span></td>		<td>Rp. <span class='pull-right'>$uangmakan</span></td>		<td>$r[jam] <span class='pull-right'>Jam</span></td>		<td>Rp. <span class='pull-right'>$uanglembur</span></td>		<td>Rp. <span class='pull-right'>$Potongan</span></td>		<td>Rp. <span class='pull-right'>$total</span></td>		</tr>";}			echo"<tfoot><tr><td colspan='9'><b>TOTAL</b></td>	<td><b>Rp. <span class='pull-right'>$jtotal</span></b></td></tr></tfoot></table>";?>