<?php defined('_FINDEX_') or die('Access Denied'); echo"<table class='table table-bordered responsive table-highlight'><thead>	<tr>		<th>NO</th>		<th>TANGGAL</th>		<th>NIM</th>		<th>NAMA MAHASISWA</th>		<th>REKANAN</th>		<th>KETERANGAN</th>		<th>JUMLAH</th>		<th>PAYMENT</th>	</tr></thead><tbody>";$no=1;$Mhsw = _query("SELECT * FROM transaksi INNER JOIN mahasiswa ON transaksi.AccID = mahasiswa.NoReg WHERE transaksi.Transaksi='17' AND transaksi.Buku IN ('8','9') AND transaksi.TglBayar BETWEEN '$dari' AND '$sampai' ORDER BY transaksi.TglBayar ASC");while ($r = _fetch_array($Mhsw)) { 	$tgle=format_tanggal($r['TglBayar']);	$JumlahBayar=digit($r['Debet']);	$namaRekanan=GetName("rekanan","RekananID",$r['RekananID'],"NamaRekanan");	$jenis=($r['RekananID']=='' OR $r['RekananID']=='0')? "REGULER": $namaRekanan;	$Payment=($r['Buku']=='9')? "TUNAI": "TRANSFER";echo"<tr>		<td>$no</td>		<td>$tgle</td>		<td>$r[NIM]</td>		<td>$r[Nama]</td>		<td>$jenis</td>		<td>$r[Uraian]</td>		<td><span class='pull-right'>$JumlahBayar</span></td>		<td>$Payment</td>	</tr>";$total +=$r['Debet'];$jtotal=digit($total);$no++;}echo"</tbody><tfoot><tr>	<td colspan='6'><b>TOTAL : </b></td>	<td><b><span class='pull-right'>$jtotal</span></b></td>	<td></td>	</tr></tfoot></table>";?>