<?phpdefined('_FINDEX_') or die('Access Denied');// Simpan Aksesif(isset($_POST['SimpanAkses'])){	$id_level		=$_POST['jabatan'];	$CekModul		=$_POST['CekModul'];	//Update Modul		delete("hakmodul","id_level='$id_level'");	$jum=count($CekModul);	for($i=0; $i < $jum; ++$i){	$idn=$CekModul[$i];	$buat = (empty($_POST['buat'.$idn])) ? 'N':'Y';	$baca = (empty($_POST['baca'.$idn])) ? 'N':'Y';	$tulis = (empty($_POST['tulis'.$idn])) ? 'N':'Y';	$hapus = (empty($_POST['hapus'.$idn]))? 'N':'Y';	$sql= "INSERT INTO hakmodul(id_level,id,buat,baca,tulis,hapus) VALUES ('$id_level','$idn','$buat','$baca','$tulis','$hapus')";	$inputModul=_query($sql) or die ("SQL Input Modul Gagal" ._error());			}alert('info','Hak Akses Berhasil di Update');CatatLog($_SESSION[Nama],'Setting Hak Akses','Hak Akses Berhasil di Update');htmlRedirect('go-akses.html',2);}