<?php defined('_FINDEX_') or die('Access Denied'); ?><?php if($baca){ function JadwalHarian($hari,$institusi,$jurusan,$program,$tahun,$sms,$buat,$baca,$edit,$hapus){$pilihAll=($hapus)? "<input type='checkbox' id='checkall'>":"No";$AksiEdit=($edit)? "<th></th>":"";echo"<div class='panel'><div class='widget-header'><h3><i class='icon-sign-blank'></i> $hari</h3></div>	<div class='panel-content panel-tables'><table class='table table-striped table-bordered table-highlight'>  	<thead>		<tr>			<th><center>No</center></th>			<th>Kode</th>			<th>Matakuliah</th>			<th>SKS</th>			<th>Dosen</th>			<th>Waktu</th>			<th>Ruang</th>			<th><center>Status</center></th>		</tr>	</thead>";		$sql="SELECT * FROM jadwal WHERE Identitas_ID='$institusi' AND Kode_Jurusan='$jurusan' AND Program_ID='$program' AND Hari='$hari' AND Tahun_ID='$tahun' AND semester='$sms' ORDER BY Jadwal_ID";		$qry= _query($sql)or die ();		while ($r=_fetch_array($qry)){  		$no++;		$sttus = ($r['Aktif'] == 'Y')? '<i class="icon-ok green"></i>' : '<i class="icon-remove red"></i>';		$ruang=GetName("ruang","ID",$r[Ruang_ID],"Nama");		$mtk = GetFields('matakuliah', 'Matakuliah_ID', $r[Kode_Mtk], '*');		$kelompok=GetName("kelompokmtk","KelompokMtk_ID",$mtk[KelompokMtk_ID],"Nama");		$dosen1=NamaDosen($r[Dosen_ID]);		$dosen2=NamaDosen($r[Dosen_ID2]);		$dosens=($r[Kode_Jurusan]=='61101')? $dosen1: "1-". $dosen1." <br>2-". $dosen2;		echo "<tr>                            			<td><center>$no</center></td>			<td>$kelompok - $mtk[Kode_mtk]</td>			<td>$mtk[Nama_matakuliah]</td>			<td>$mtk[SKS]</td>			<td>$dosens</td>			<td>$r[Jam_Mulai] - $r[Jam_Selesai]</td>			<td>$ruang</td>			<td><center>$sttus</center></td>			</tr>";        	}echo"</table></div></div>";}	$jabatan 	= $_SESSION[Jabatan];	$m = GetFields('mahasiswa', 'NIM', $_SESSION['yapane'], 'IDProg');	$tAktif=TahunAktif();	$institusi= $_SESSION['Identitas'];	$jurusan=($jabatan==18 OR $jabatan==19)? $_SESSION['prodi']:$_POST['jurusan'];	$disjurusan=($jabatan==18 OR $jabatan==19)? "disabled":"";	$program=($jabatan==19)? $m['IDProg']:$_POST['program'];    $disprogram=($jabatan==19)? "disabled":"";    $tahun= (empty($_POST['tahun'])) ? $tAktif : $_POST['tahun'];    $sms= (empty($_POST['semester'])) ? 1: $_POST['semester'];$PRODI=NamaProdi($jurusan);$kelas=NamaKelasa($program);?><div class="row"><div class="span12"><form method="post" id="form">	   <div class="widget widget-table">					<div class="widget-header">												<h3>							<i class="icon-calendar"></i>							JADWAL KULIAH | STIE YAPAN SURABAYA									</h3>					<div class="widget-actions">						<div class="btn-group">						<?php if($buat){ ?>							<a href="aksi-jadwalKuliah-AllJadwal.html" class="btn btn-small btn-success"><i class="icon-edit"></i> Kelola Jadwal</a>						<?php } ?>						<?php if(!empty($tahun) AND !empty($program) AND !empty($jurusan) AND !empty($sms)){								echo"<a class='btn btn-inverse ui-lightbox' title='JADWAL KULIAH - PRODI $PRODI' href='di-print-JadwalKuliah-$tahun-$program-$jurusan-$sms.html?lightbox[iframe]=true&ui-lightbox[width]=75p&ui-lightbox[height]=75p'>Cetak</a>";								} ?>				        </div>								</div> <!-- /.widget-actions -->					</div> <!-- /widget-header --><div class="widget-toolbar"><div class="row-fluid"><div class="pull-left"  style="padding-top: 7px"><div class='input-prepend input-append'><?php echo"<span class='add-on'> TAHUN </span><select name='tahun' onChange='this.form.submit()' class=''>		<option value=''>- Tahun -</option>";		$sqlp="SELECT * FROM tahun WHERE Identitas_ID='$institusi'";		$qryp=mysql_query($sqlp)  or die();		while ($d2=mysql_fetch_array($qryp)){		$cek= ($d2['Tahun_ID']==$tahun) ? 'selected' : '';		echo "<option value='$d2[Tahun_ID]' $cek> $d2[Nama] </option>";		}		echo " </select><span class='add-on'> PRODI </span>		<select name='jurusan' onChange='this.form.submit()' class='' $disjurusan>		<option value=''>- Pilih Prodi -</option>";		$sqlp="SELECT * FROM jurusan WHERE Identitas_ID='$institusi'";		$qryp=mysql_query($sqlp)  or die();		while ($d1=mysql_fetch_array($qryp)){		$cek= ($d1['kode_jurusan']==$jurusan) ? 'selected' : '';		echo "<option value='$d1[kode_jurusan]' $cek>  $d1[nama_jurusan]</option>";		}		echo"</select><span class='add-on'> KELAS </span>		<select name='program' onChange='this.form.submit()' class='' $disprogram>		<option value=''>- Pilih Kelas -</option>";		$sqlp="SELECT * FROM program WHERE Identitas_ID='$institusi'";		$qryp=mysql_query($sqlp) or die();		while ($d2=mysql_fetch_array($qryp)){		$cek= ($d2['ID']==$program) ? 'selected' : '';		echo "<option value='$d2[ID]' $cek>  $d2[nama_program]</option>";		}		echo"</select><span class='add-on'> SEMESTER </span>		<select name='semester' onChange='this.form.submit()' class=''>		<option value=''>- Pilih Semester -</option>";		$pu=($jurusan=='61101') ? 4:8;		echo Semester(1,$pu, $sms);		echo " </select>";?></div></div></div></div><div class="widget-content"><?php JadwalHarian("Senin",$institusi,$jurusan,$program,$tahun,$sms,$buat,$baca,$edit,$hapus);	JadwalHarian("Selasa",$institusi,$jurusan,$program,$tahun,$sms,$buat,$baca,$edit,$hapus);	JadwalHarian("Rabu",$institusi,$jurusan,$program,$tahun,$sms,$buat,$baca,$edit,$hapus);	JadwalHarian("Kamis",$institusi,$jurusan,$program,$tahun,$sms,$buat,$baca,$edit,$hapus);		JadwalHarian("Jumat",$institusi,$jurusan,$program,$tahun,$sms,$buat,$baca,$edit,$hapus);		JadwalHarian("Sabtu",$institusi,$jurusan,$program,$tahun,$sms,$buat,$baca,$edit,$hapus);	?></div></div></form></div></div><?php }else{ErrorAkses();} ?>