<?phpdefined('_FINDEX_') or die('Access Denied');//simpan Krsif(isset($_POST['simpanKrs'])) {	$source = @$_POST['Kode_mtk'];	$source = multipleSelect($source);	if(empty($source)){	alert('error','Anda Belum Memilih Matakuliah');	CatatLog($_SESSION[Nama],'Tambah Krs','Gagal Menambah Krs Belum Dipilih');	}else{	$pil = explode(",",$source);	foreach($pil as $id)	{		$cek=_num_rows(_query("SELECT * FROM krs WHERE NIM='$_POST[NIM]' AND Tahun_ID='$_POST[tahun]' AND Jadwal_ID='$id' AND semester='$_POST[semester]' AND kelas='$_POST[kelas]' "));        if ($cek > 0){		$pilih= false;		}else{		$pilih= true;		$insert= _query("INSERT INTO krs(NIM,Tahun_ID,Jadwal_ID,semester,kelas,SKS) 					VALUES('$_POST[NIM]', '$_POST[tahun]', '$id','$_POST[semester]','$_POST[kelas]', '$_POST[sks]')"); 		}	}	if($pilih){		alert('info','KRS Berhasil di Tambahkan');		CatatLog($_SESSION[Nama],'Tambah KRS','KRS Berhasil di Tambahkan');		htmlRedirect('go-krs.html',2);	}else{		alert('error','KRS Gagal di Tambahkan Program Mencoba Entrykan Data yang Sama');		CatatLog($_SESSION[Nama],'Tambah KRS','KRS Gagal di Tambahkan Program Mencoba Entrykan Data yang Sama');		}	}}// Hapus KRSif(isset($_POST['deleteKrs'])){	$source = @$_POST['check'];	$source = multipleSelect($source);	if(empty($source)){	alert('error','Pilih KRS yang Akan di Hapus');	CatatLog($_SESSION[Nama],'Hapus Data KRS','Gagal Meng-Hapus KRS Belum Dipilih');	}else{	$del = explode(",",$source);	foreach($del as $id)	{		$tabel="krs";		$kondisi="KRS_ID='$id'";		$delete = delete($tabel,$kondisi);		}	alert('info','Data KRS Berhasil di Hapus');	CatatLog($_SESSION[Nama],'Hapus Data KRS','Data KRS Berhasil di Hapus');	}}