<?phpdefined('_FINDEX_') or die('Access Denied');$db = new FQuery();  $db -> connect();//Tambah dan Edit Modulif(isset($_POST['SimpanModule'])) { 		$md 			= $_POST['md']+0;	$id				= $_POST['id'];	$nama_modul 	= $_POST['nama_modul']; 	$url 			= $_POST['url'];   	$menu_order		= $_POST['menu_order'];        	$keterangan 	= $_POST['keterangan'];	$aktif 			= empty($_POST['aktif']) ? 'N' : $_POST['aktif'];  if ($md == 0) {	$relasi_modul	= empty($_POST['id_group']) ? '0' : $_POST['id_group'];	$qr=$db->update('modul',array("parent_id"=>"$relasi_modul",		'judul'		=>"$nama_modul",		'url'		=>"$url",		'menu_order'=>"$menu_order",		'keterangan'=>"$keterangan",		'class'		=>"$_POST[class]",		'aktif'		=>"$aktif"),		'id='.$_POST['id']); 	if($qr AND isset($_POST['SimpanModule'])){				alert('info','Modul Berhasil di Update');		CatatLog($_SESSION[Nama],'Update Modul','Modul Berhasil di Update');		htmlRedirect('go-modul.html',1);	}  } else {    $ada = GetFields('modul', 'id', $id, '*');    if (empty($ada)) {		if($aktif=='N'){		$relasi_modul	= empty($_POST['id_group']) ? '999' : $_POST['id_group'];		}else{		$relasi_modul	= empty($_POST['id_group']) ? '0' : $_POST['id_group'];		}		$qr=$db->insert('modul',array("","$relasi_modul","","$nama_modul","$url","$menu_order","$keterangan","$_POST[class]","$aktif")); 		if($qr AND isset($_POST['SimpanModule'])){			alert('info','Modul Baru Berhasil di Simpan');		CatatLog($_SESSION[Nama],'Tambah Modul','Modul Berhasil di Simpan');		htmlRedirect('go-modul.html',1);		}    } else{	alert('error','Modul Gagal disimpan.Modul Sudah ada dalam database');	CatatLog($_SESSION[Nama],'Tambah Modul','Modul Gagal disimpan.Modul Sudah ada dalam database');	}  }}// Hapus Modulif(isset($_POST['delete'])){	$source = @$_POST['check'];	$source = multipleSelect($source);	$delete = multipleDelete('modul',$source);		if(isset($delete)){		alert('info','Modul Berhasil di Hapus');		CatatLog($_SESSION[Nama],'Hapus Modul','Modul Berhasil di Hapus');	}else{		alert('error','Pilih Modul yang Akan di Hapus');		CatatLog($_SESSION[Nama],'Hapus Modul','Gagal Menghapus Modul, Pilih Modul yang Akan di Hapus');	}}