<?phpdefined('_FINDEX_') or die('Access Denied');// Proses Registrasiif(isset($_POST['register'])){$source = @$_POST['checkreg'];$source = multipleSelect($source);	if(empty($source)){	alert('error','Pilih Data Mahasiswa yang Akan di Registrasi Ulang');	CatatLog($_SESSION[Nama],'Registrasi ulang Mahasiswa','Gagal Registrasi Ulang Mahasiswa Belum Dipilih');	}else{	$reg = explode(",",$source);	foreach($reg as $id)	{		$cek=_num_rows(_query("SELECT * FROM regmhs WHERE Tahun='$_POST[tahun]' AND NIM='$id'"));		if ($cek > 0){			$update=false;		}else{			$update=true;			$tglsekarang=date("Ymd");    			$insert= _query("INSERT INTO regmhs(Tahun,Identitas_ID,Jurusan_ID,NIM,tgl_reg,aktif) VALUES('$_POST[tahun]','$_POST[Identitas]','$_POST[Jurusan]','$id','$tglsekarang','Y')");		} 	}		if($update){			alert('info','Registrasi Ulang Mahasiswa Berhasil Di proses');			CatatLog($_SESSION[Nama],'Registrasi Ulang Mahasiswa','Registrasi Ulang Mahasiswa Berhasil di proses');		}else{			alert('error','Registrasi Ulang Mahasiswa Gagal Di proses. Data REGISTRASI Sudah ada dalam database.');			CatatLog($_SESSION[Nama],'Registrasi Ulang Mahasiswa Gagal','Data REGISTRASI Sudah ada dalam database.');		}	}}// Hapus Registrasiif(isset($_POST['delete'])){$source = @$_POST['checkUnreg'];$source = multipleSelect($source);	if(empty($source)){	alert('error','Pilih Data Mahasiswa yang Akan di hapus Registrasi Ulangnya');	CatatLog($_SESSION[Nama],'Registrasi ulang Mahasiswa','Gagal Registrasi Ulang Mahasiswa Belum Dipilih');	}else{	$reg = explode(",",$source);	foreach($reg as $id)	{		$tabel="regmhs";		$kondisi="ID_Reg='$id'";		$delete = delete($tabel,$kondisi);	 	}		alert('info','Registrasi Ulang Mahasiswa Berhasil Di Hapus');		CatatLog($_SESSION[Nama],'Registrasi Ulang Mahasiswa','Registrasi Ulang Mahasiswa Berhasil di Hapus');	}}