<?php defined('_FINDEX_') or die('Access Denied'); //Startif($baca){$Identitas=$_SESSION[Identitas];$pilihAll=($hapus OR $edit OR $buat)? "<input type='checkbox' id='checkall'>":"No";$pilihAlls=($hapus OR $edit OR $buat)? "<input type='checkbox' id='checkalls'>":"No";$tAktif		=TahunAktif();$tahun 		= (empty($_POST[tahun]))? $tAktif : $_POST[tahun];$prodi 		= ($_SESSION[Jabatan]==18)? $_SESSION[prodi]: $_POST[jurusan];$disnim		= ($_SESSION[Jabatan]==18)? "disabled": "";$NamaProdi =NamaProdi($prodi);$Btnhapus=($hapus)? "<button name='delete' type='submit' class='btn btn-danger ui-tooltip' data-placement='bottom' title='Hapus / Batalkan registrasi ulang'>							<i class='icon-trash'></i> 							Hapus						</button>":"";$BtnEdit=($buat OR $edit)? "<button name='register' type='submit' class='btn btn-success ui-tooltip' data-placement='bottom' title='Proses Registrasi'>							<i class='icon-ok'></i> 							Register						</button>":"";//fuctionfunction TotalReg($tahun,$prodi){global $saiki;$tampil=_num_rows(_query("SELECT * FROM mahasiswa a,regmhs b WHERE a.NIM=b.NIM AND a.kode_jurusan='$prodi' AND b.Tahun='$tahun'"));$jumlah=($tampil > 0)? "&nbsp; &nbsp; <span class='badge badge-success'> $tampil</span>" : "";return $jumlah;}function TotalUnReg($tahun,$prodi){global $saiki;$tampil=_num_rows(_query("SELECT * FROM mahasiswa WHERE NIM NOT IN (SELECT NIM FROM regmhs WHERE Tahun='$tahun') AND kode_jurusan='$prodi' AND NIM!='' AND LulusUjian='N'"));$jumlah=($tampil > 0)? "&nbsp; &nbsp; <span class='badge badge-warning'> $tampil</span>" : "";return $jumlah;}?><div class="row"><div class="span12"><form method='post'>	<div class="widget widget-table">		<div class="widget-header">									<h3><i class="icon-table"></i>DATA REGISTRASI ULANG | PRODI <?php echo $NamaProdi;?></h3>			<div class="widget-actions"><?php echo"<div class='input-prepend input-append pull-left btn-group'>		<select name='tahun' onChange='this.form.submit()' class='ui-tooltip' data-placement='bottom' title='Pilih Tahun Akademik'>			<option value=''>- PILIH TAHUN -</option>";			$t=_query("SELECT * FROM tahun ORDER BY Nama DESC");			while($r=_fetch_array($t)){			$cek=($tahun == $r['Tahun_ID'])? 'selected': '';			echo "<option value=$r[Tahun_ID] $cek> $r[Nama]</option>";			}		echo "</select>		<select name='jurusan' onChange='this.form.submit()' $disnim class='ui-tooltip' data-placement='bottom' title='Pilih Program Studi'>		<option value=''> -- PILIH PRODI --</option>";		$sqlp="SELECT * FROM jurusan WHERE Identitas_ID='$_SESSION[Identitas]'";		$qryp=_query($sqlp)or die();		while ($d1=_fetch_array($qryp)){			if ($d1['kode_jurusan']==$prodi){ $cek="selected";  }  else{ $cek=""; }			echo "<option value='$d1[kode_jurusan]' $cek>$d1[nama_jurusan]</option>";		}		echo"</select> $Btnhapus $BtnEdit";?></div></div></div> <div class="widget-tabs">	<ul class="nav nav-tabs">		<li class="active">					<a href="#UnRegistereds" class="ui-tooltip" data-placement="top" title="Data Mahasiswa <?php echo $NamaProdi;?> Yang Belum Registrasi Ulang">				<i class="icon-remove"></i>				UnRegistered					<?php echo TotalUnReg($tahun,$prodi);?>			</a>			</li>		<li>			<a href="#Registereds" class="ui-tooltip" data-placement="top" title="Data Mahasiswa <?php echo $NamaProdi;?> Yang Sudah Registrasi Ulang">				<i class="icon-ok"></i> 				Registered				<?php echo TotalReg($tahun,$prodi);?>			</a>				  				</li>	</ul></div> <div class="widget-content"><?phpecho"<input type='hidden' name='tahun' value='$tahun'>	<input type='hidden' name='Jurusan' value='$prodi'> 	<input type='hidden' name='Identitas' value='$Identitas'>";echo"<div class='tab-content'>	<div class='tab-pane active' id='UnRegistereds'>";	echo"<table class='table table-striped table-bordered table-highlight' id='UnRegistered'>	<thead>		<tr>			<th><center>$pilihAll</center></th>			<th>NIM</th>			<th>NAMA</th>			<th>PRODI</th>			<th>KELAS</th>			<th>ANGKTAN</th>		</tr>	</thead>	<tbody>";	//$nos=0;	$qry= _query("SELECT * FROM mahasiswa WHERE NIM NOT IN (SELECT NIM FROM regmhs WHERE Tahun='$tahun') AND kode_jurusan='$prodi' AND NIM!='' AND LulusUjian='N' ORDER BY Nama, NIM ASC")or die ();	while ($r=_fetch_array($qry)){  	$jurusan=NamaProdi($r['kode_jurusan']);	$kelas=GetName("program","ID",$r['IDProg'],"nama_program");	$angkatan=GetName("tahun","Tahun_ID",$r['Angkatan'],"Nama");	$AngkatThn = substr($angkatan, 15, 9); 	$check ="<input type='checkbox' name='checkreg[]' value='$r[NIM]' rel='ck'>";	$pilih=($hapus)? $check:$nos;	echo "<tr>              			<td><center>$pilih</center></td>            			<td>$r[NIM]</td>			<td>$r[Nama]</td>			<td>$jurusan</td>			<td>$kelas</td>			<td>$AngkatThn</td>		</tr>";$nos++; 	}  echo"</tbody></table>";		echo"</div>		<div class='tab-pane' id='Registereds'>";			echo"<table class='table table-striped table-bordered table-highlight' id='Registered'>	<thead>		<tr>			<th><center>$pilihAlls</center></th>			<th>NIM</th>			<th>NAMA</th>			<th>PRODI</th>			<th>KELAS</th>			<th>ANGKTAN</th>		</tr>	</thead>	<tbody>";	$ne=1;	$qry= _query("SELECT * FROM mahasiswa a,regmhs b WHERE a.NIM=b.NIM AND a.kode_jurusan='$prodi' AND b.Tahun='$tahun' ORDER BY a.Nama, a.NIM ASC")or die ();	while ($r=_fetch_array($qry)){  	$jurusan=GetName("jurusan","kode_jurusan",$r['kode_jurusan'],"nama_jurusan");	$kelas=GetName("program","ID",$r['IDProg'],"nama_program");	$angkatan=GetName("tahun","Tahun_ID",$r['Angkatan'],"Nama");	$AngkatThn = substr($angkatan, 15, 9); 	$check ="<input type='checkbox' name='checkUnreg[]' value='$r[ID_Reg]' rel='ck'>";	$pilih=($hapus)? $check:$ne;	echo "<tr>              			<td><center>$pilih</center></td>            			<td>$r[NIM]</td>			<td>$r[Nama]</td>			<td>$jurusan</td>			<td>$kelas</td>			<td>$AngkatThn</td>		</tr>";$ne++; 	}  echo"</tbody></table>";echo"</div></div>";?></div></div></form></div></div><?php }else{ErrorAkses();} ?>