<?phpdefined('_FINDEX_') or die('Access Denied');//Tambah dan Edit Rekananif(isset($_POST['simpanRekanan'])) {	$md	= $_POST['md']+0;if ($md == 0) {	$update=_query("UPDATE rekanan SET 			NamaRekanan  = '$_POST[NamaRekanan]',			Institusi		= '$_POST[Institusi]',			Alamat			= '$_POST[Alamat]',			Kota			= '$_POST[Kota]',			Kodepos     	= '$_POST[Kodepos]',			Telepon     	= '$_POST[Telepon]',			Fax     		= '$_POST[Fax]'			WHERE RekananID = '$_POST[ID]'");	alert('info','Rekanan Berhasil Di Update');	CatatLog($_SESSION[Nama],'Update Rekanan','Rekanan Berhasil Di Update');		htmlRedirect('go-rekanan.html',1);} else {	$cek=_num_rows(_query("SELECT * FROM rekanan  WHERE NamaRekanan='$_POST[NamaRekanan]'"));        if ($cek > 0){			alert('error','Gagal Menambah Rekanan. Data Sudah ada dalam Database');			CatatLog($_SESSION[Nama],'Tambah Rekanan','Gagal Menambah Rekanan. Data Sudah ada dalam Database.');        } else {			$insert = _query( "INSERT INTO rekanan(NamaRekanan,Institusi,Alamat,Kota,Kodepos,Telepon,Fax)			VALUES('$_POST[NamaRekanan]','$_POST[Institusi]','$_POST[Alamat]','$_POST[Kota]','$_POST[Kodepos]','$_POST[Telepon]','$_POST[Fax]')");			alert('info','Rekanan Berhasil Disimpan');			CatatLog($_SESSION[Nama],'Tambah Rekanan','Rekanan Berhasil Disimpan');				htmlRedirect('go-rekanan.html',1);        }	}	}// Hapus Rekananif(isset($_POST['delete'])){	$source = @$_POST['check'];	$source = multipleSelect($source);	if(empty($source)){	alert('error','Pilih Rekanan yang Akan di Hapus');	CatatLog($_SESSION[Nama],'Hapus Data Rekanan','Gagal Meng-Hapus Rekanan Belum Dipilih');	}else{	$del = explode(",",$source);	foreach($del as $id)	{		$tabel="rekanan";		$kondisi="RekananID='$id'";		$delete = delete($tabel,$kondisi);		}	alert('info','Data Rekanan Berhasil di Hapus');	CatatLog($_SESSION[Nama],'Hapus Data Rekanan','Data Rekanan Berhasil di Hapus');	htmlRedirect('go-rekanan.html',1);	}}